package loopingstatements;

public class Foreachdemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] num = {10, 20, 30, 40};
		for (int i : num) {
			  System.out.println(i);
			}


		String[] cars = {"Volvo", "BMW", "Ford", "benz"};
		for (String i : cars) {
		  System.out.println(i);
		}
	}

}
