package work;

public class ZArrayPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] arr1 = new int[5];
		arr1[0] = 1;
		arr1[1] = 2;
		arr1[2] = 4;
		arr1[3] = 5;
		arr1[4] = 6;		
		
		for (int w : arr1) {
			System.out.println("array  value - " +  w);
		}
		
		int[] arr = {5,2,3,4,5,6};
		
		for (int k=0; k<arr.length; k++) {
			System.out.println(arr[k]);
		}
		
		
		
		
	}

}
